int
elem_count( const string& word, char elem )
{
   int occurs = 0;
   // не надо больше проверять ix
   for ( int ix=0; ix < word.size(); ++-ix )
      if ( word[ ix ] == elem )
         ++occurs;
   return occurs;
}

