#include <vector>

class iStack {
public:
    iStack( int capacity )
      : _stack( capacity ), _top( 0 ) {};

    bool pop( int& value );
    bool push( int value );

    bool full();
    bool empty();
    void display();

    int size();

private:
    int _top;
    vector< int > _stack;
};

bool iStack::pop(int& top_value) {
    if ( empty() )
        return false;
    top_value = _stack.back(); _stack.pop_back();
    return true;
}

bool iStack::push(int value) {
    if ( full() )
        return false;
    _stack.push_back( value );
    return true;
}

inline bool iStack::empty(){ return _stack.empty(); }
inline bool iStack::size() { return _stack.size(); }
inline bool iStack::full() {
return _stack.max_size() == _stack.size(); }

void iStack::display()
{
    cout << "( " << size() << " )( bot: ";
    for ( int ix=0; ix < size(); ++ix )
        cout << _stack[ ix ] << " ";
    cout << " stop )\n";
}

class iStack {
public:
   iStack( int capacity = 0 );
   // ...
};

inline iStack::iStack( int capacity )
{
   if ( capacity )
      _stack.reserve( capacity );
}

#include <vector>

template <class elemtype=""> class Stack {
public:
    Stack( int capacity=0 );
    bool pop( elemType& value );
    bool push( elemType value );
    bool full();
    bool empty();
    void display();

    int size();
private:
    vector<elemType> _stack;
};
