inline int abs( int obj ) {
    // возвращает абсолютное значение iobj
    return( iobj < 0 ? -iobj : iobj);
}
inline int min(int p1, int p2) {
    // возвращает меньшую из двух величин
    return( pi < p2 ? pi : p2 );
}

int gcd(int vl, int v2) {
    // возвращает наибольший общий делитель
    while (v2) {
        int temp = v2;
        v2 = vl % v2;
        vl = temp;
    }
    return vl;
}

