#include <iostream>
void swap( int, int );
int main() {
  int i = 10;
  int j = 20;
  cout << "Перед swap():\ti: "
    << i << "\tj: " << j << endl;

  swap( i, j );

  cout << "После swap():\ti: "
      << i << "\tj: " << j << endl;

  return 0;
}

// pswap() обменивает значения объектов,
// адресуемых указателями vl и v2
void pswap( int *vl, int *v2 ) {
   int tmp = *v2;
   *v2 = *vl;
   *vl = tmp;
}

// rswap() обменивает значения объектов,
// на которые ссылаются vl и v2
void rswap(int& vl, int& v2) {
   int tmp = v2;
   v2 = vl;
   vl = tmp;
}

#include <vector>

// параметр-ссылка 'occurs'
// содержит второе возвращаемое значение

vector<int>::const_iterator look_up(const vector<int>& vec,
    int value,    // искомое значение
    int& occurs)  // количество вхождений
{
    // res_iter инициализируется значением
    // следующего за конечным элемента
    vector<int>::const_iterator res_iter = vec.end();
    occurs = 0;

    for ( vector<int>::const_iterator iter = vec.begin();
             iter != vec.end();
             ++iter )
        if ( *iter == value ) {
            if ( res_iter == vec.end() )
                res_iter = iter;
            ++occurs;
        }

    return res_iter;
}

