#include <iostream>

const lineLength =12; // количество элементов в строке
void putValues( int *ia, int sz) {
    cout << "( " << sz << " )< ";
    for (int i=0;i < sz; ++i )
    {
        if ( i % lineLength == 0 && i)
            cout << "\n\t"; // строка заполнена

        cout << ia[ i ];

        // разделитель, печатаемый после каждого элемента,
        // кроме последнего
        if ( i % lineLength != lineLength-1 &&
                 i != sz-1 )
            cout << ", ";
    }
    cout << " >\n";
}

