    vector<int> vec1(1); // создадим вектор из 1 элемента
    vecl[0] = i;
    putValues( vecl );

    vector<int> vec2;    // создадим пустой вектор
    // добавим элементы к vec2
    for ( int ix = 0; ix < sizeof(j) / sizeof(j[0]); ++ix)
      // vec2[ix] == j [ix]
      vec2.push_back( j[ix] );
    putValues( vec2 );

    return 0;
}

