#include <iostream>

int min( int*, int );
int (*pf)( int*, int ) = min;

const int iaSize = 5;
int ia[ iaSize ] = { 7, 4, 9, 2, 5 };

int main() {
    cout << "Прямой вызов: min: "
         << min( ia, iaSize ) << endl;

    cout << "Косвенный вызов: min: "
         << pf( ia, iaSize ) << endl;

    return 0;
}

int min( int* ia, int sz ) {
    int minVal = ia[ 0 ];
    for ( int ix = 1; ix < sz; ++ix )
        if ( minVal &gt; ia[ ix ] )
            minVal = ia[ ix ];
    return minVal;
}

