IntArray::IntArray (int sz)
{
   // инициализация членов данных
   _size = sz;
   ia = new int[_size];

   // инициализация элементов массива
   for (int ix=0; ix<_size; ++ix)
     ia[ix] = 0;
}

IntArray::IntArray (int *array, int sz)
{
   // инициализация членов данных
   _size = sz;
   ia = new int[_size];

  // инициализация элементов массива
  for (int ix=0; ix<_size; ++ix)
     ia[ix] = array[ix];
}

IntArray array;
// следующие два объявления совершенно эквивалентны:
