inline IntArrayRC::IntArrayRC( const int *iar, int sz )
   : IntArray( iar, sz ) {}

#include <iostream>
#include "IntArray.h"
#include "IntArrayRC.h"

void swap( IntArray &ia, int ix, int jx )
{
  int tmp = ia[ ix ];
  ia[ ix ] = ia[ jx ];
  ia[ jx ] = tmp;
}

