{
   int *pstats = new int [4];
   try {
      pstats[0] = sum_it (ia,size);
      pstats[1] = min_val (ia,size);
      pstats[2] = max_val (ia,size);
   }
   catch (string exceptionMsg) {
       // код обработчика
   }
   catch (const statsException &statsExcp) {
      // код обработчика
   }

   pstats [3] = pstats[0] / size;
   do_something (pstats);

   return pstats;
}

